if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/yskkyle/OneDrive/Documents/projects/base-score/packages/base_/bin/node_modules:/mnt/c/Users/yskkyle/OneDrive/Documents/projects/base-score/packages/base_/node_modules:/mnt/c/Users/yskkyle/OneDrive/Documents/projects/base-score/packages/node_modules:/mnt/c/Users/yskkyle/OneDrive/Documents/projects/base-score/node_modules:/mnt/c/Users/yskkyle/OneDrive/Documents/projects/node_modules:/mnt/c/Users/yskkyle/OneDrive/Documents/node_modules:/mnt/c/Users/yskkyle/OneDrive/node_modules:/mnt/c/Users/yskkyle/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/yskkyle/OneDrive/Documents/projects/base-score/node_modules/.pnpm/node_modules"
