import { d as defineCommand, e as legacyRootDirArgs, b as dotEnvArgs, l as logLevelArgs, c as cwdArgs, O } from '../shared/nuxi.DCVnYqfZ.mjs';
import { l as loadKit, c as createJiti } from '../shared/nuxi.BxIDvNqq.mjs';
import { x as ve } from './main.mjs';
